$(document).ready(inicio);

function inicio() {
    $("#yes").click(VotaSi);
}
var colorP1 = "green";

function VotaSi() {
    switch (colorP1) {
        case "green":
            colorP1 = "red";
            $("#yes").attr("src", "http://www.freeiconspng.com/uploads/like-button-png-3.png");
            break;
        case "red":
            colorP1 = "green";
            break;
    }
    $(this).css({
        "background-color": colorP1
    });
}


//searcher
$(document).ready(function() {

    var consulta;

    //hacemos focus al campo de búsqueda
    $("#busqueda").focus();

    //comprobamos si se pulsa una tecla
    $("#busqueda").keyup(function(e) {

        //obtenemos el texto introducido en el campo de búsqueda
        consulta = $("#busqueda").val();

        //hace la búsqueda

        $.ajax({
            type: "POST",
            url: "buscar.php",
            data: "b=" + consulta,
            dataType: "html",
            beforeSend: function() {
                //imagen de carga
                $("#resultado").html("<p align='center'><img src='ajax-loader.gif' /></p>");
            },
            error: function() {
                alert("error petición ajax");
            },
            success: function(data) {
                $("#resultado").empty();
                $("#resultado").append(data);

            }
        });


    });

});

